/* eslint-disable @typescript-eslint/no-explicit-any */
/**
 * @fileoverview Defines the IAdmin interface for admin data.
 * @version 1.0.0
 * @module adminInterface
 */

import { Optional } from 'sequelize';

/**
 * Interface representing the attributes of an admin.
 */
interface IAdmin {
  [x: string]: any;
  id?: number;
  first_name?: string;
  last_name?: string;
  user_id?: number;
  createdAt?: Date;
  updatedAt?: Date;
}

/**
 * Interface for creating a new admin (excluding autogenerated fields).
 */
type IAdminCreation = Optional<IAdmin, 'id' | 'createdAt' | 'updatedAt'>;

export { IAdmin, IAdminCreation };
